## 一，需求

利用QML实现无边框窗体，要求可移动，可缩放。

## 二，效果

![img](assets/ai.gif)

## 三，实现

```python
//主窗体
import QtQuick 2.12
import QtQuick.Controls 2.3
import QtQuick.Window 2.3

import "./widget"

Window  {
    id: window
    visible: true
    flags: Qt.FramelessWindowHint|Qt.Window
    width: 1024
    height: 768
    color: "#0F1013"
    property int bw: 3

    //改变鼠标形状
    MouseArea {
        anchors.fill: parent
        hoverEnabled: true
        cursorShape: {
            const p = Qt.point(mouseX, mouseY)
            const b = bw + 10
            if (p.x < b && p.y < b) return Qt.SizeFDiagCursor
            if (p.x >= width - b && p.y >= height - b) return Qt.SizeFDiagCursor
            if (p.x >= width - b && p.y < b) return Qt.SizeBDiagCursor
            if (p.x < b && p.y >= height - b) return Qt.SizeBDiagCursor
            if (p.x < b || p.x >= width - b) return Qt.SizeHorCursor
            if (p.y < b || p.y >= height - b) return Qt.SizeVerCursor
        }
        acceptedButtons: Qt.NoButton
    }

    DragHandler {
        id: resizeHandler
        grabPermissions: TapHandler.TakeOverForbidden
        target: null
        onActiveChanged: if (active) {
            const p = resizeHandler.centroid.position
            const b = bw + 10
            let e = 0;
            if (p.x < b) { e |= Qt.LeftEdge }
            if (p.x >= width - b) { e |= Qt.RightEdge }
            if (p.y < b) { e |= Qt.TopEdge }
            if (p.y >= height - b) { e |= Qt.BottomEdge }
            window.startSystemResize(e);
        }
    }

    MyToolBar{
        window:window
    }
}
//工具栏
import QtQuick 2.12
import QtQuick.Controls 2.3
import QtQuick.Window 2.3

ToolBar {
    height: 50
    anchors.top: parent.top
    anchors.left: parent.left
    anchors.right: parent.right
    x:bw
    y:bw

    property var window: null
    //关闭 最大化 最小化按钮
    function toggleMaximized(mode) {
        if(mode===0){
            if (window.visibility === Window.Maximized) {
                window.showNormal();
            } else {
                window.showMaximized();
            }
        }else if(mode===1){
            window.showMinimized()
        }else if(mode===2){
            window.close()
        }else if(mode===4){
            return window.visibility
        }

    }

    Rectangle{
        id:rectBk
        anchors.fill: parent
        color: "#1F2025"
        MouseArea {
            anchors.fill: parent
            acceptedButtons: Qt.LeftButton
            property point clickPos: "0,0"
            onPressed: {
                clickPos = Qt.point(mouse.x, mouse.y)
            }
            onDoubleClicked: {
                toggleMaximized(0)
            }
            onPositionChanged: {
                //鼠标偏移量
                var delta = Qt.point(mouse.x-clickPos.x, mouse.y-clickPos.y)

                window.setX(window.x+delta.x)
                window.setY(window.y+delta.y)
            }
        }
    }

    Row{
        anchors.right: parent.right
        anchors.top: parent.top
        anchors.bottom: parent.bottom
        spacing: 0
        Rectangle{
            width: 50
            height: 50
            color: Qt.rgba(0,0,0,0)
            Image {
                anchors.centerIn: parent
                id: min
                source: "qrc:/image/icon/minus.png"
                width: 26
                height: 26
            }
            MouseArea{
                anchors.fill: parent
                hoverEnabled: true
                onClicked: {
                    toggleMaximized(1)
                }
                onEntered: {
                    parent.color = "#454B57"
                }

                onExited: {
                    parent.color = Qt.rgba(0,0,0,0)
                }
            }
        }

        Rectangle{
            width: 50
            height: 50
            color: Qt.rgba(0,0,0,0)
            Image {
                id: max
                source: (toggleMaximized(4) === Window.Maximized)?"qrc:/image/icon/maximize.png":"qrc:/image/icon/maximize2.png"
                anchors.centerIn: parent
                width: 26
                height: 26
            }
            MouseArea{
                anchors.fill: parent
                hoverEnabled: true
                onClicked: {
                    console.log("max")
                    toggleMaximized(0)
                }
                onEntered: {
                    parent.color = "#454B57"
                }
                onExited: {
                    parent.color = Qt.rgba(0,0,0,0)
                }
            }
        }

        Rectangle{
            width: 50
            height: 50
            color: Qt.rgba(0,0,0,0)
            Image {
                id: close
                source: "qrc:/image/icon/close.png"
                anchors.centerIn: parent
                width: 26
                height: 26
            }
            MouseArea{
                anchors.fill: parent
                hoverEnabled: true
                onClicked: {
                    toggleMaximized(2)
                }
                onEntered: {
                    parent.color = "#454B57"
                }

                onExited: {
                    parent.color = Qt.rgba(0,0,0,0)
                }
            }
        }

    }

    //标题
    Text {
        id:title
        anchors.left: parent.left
        anchors.verticalCenter:  parent.verticalCenter
        anchors.leftMargin: 20
        color: "#FFFFFF"
        font.pixelSize: 24
        text: qsTr("影像检测仪")
    }

    Menu {
        id:settingMenu
        x:setting.x
        y:setting.y+setting.height+1
        width: 120
        Action {
            text: qsTr("创建项目")
            onTriggered: console.log("创建项目")

        }
        Action {
            text: qsTr("编辑项目")
            onTriggered: console.log("编辑项目")
        }

        delegate: MenuBarItem {
            id: settingMenuItem
            height: 40
            width: 120

            contentItem: Text {
                text: settingMenuItem.text
                font.pixelSize: 16
                color:  "#ffffff"
                horizontalAlignment: Text.AlignHCenter
                verticalAlignment: Text.AlignVCenter
                elide: Text.ElideRight
            }

            background: Rectangle {
                implicitWidth: 120
                implicitHeight: 40
                color: Qt.rgba(0,0,0,0)
                MouseArea{
                    anchors.fill: parent
                    hoverEnabled: true
                    propagateComposedEvents: true
                    onClicked: {
                        settingMenu.close()
                        settingMenuItem.action.trigger()
                    }
                    onEntered: {
                        parent.color = "#454B57"
                    }

                    onExited: {
                        parent.color = Qt.rgba(0,0,0,0)
                    }
                }
            }
        }
        background: Rectangle {
            implicitWidth: 150
            implicitHeight: 40
            color: "#1F2025"
        }
    }

    //设置
    Rectangle{
        id:setting
        anchors.top: parent.top
        anchors.bottom: parent.bottom
        anchors.left:title.right
        anchors.leftMargin: 100
        color: Qt.rgba(0,0,0,0)
        width: 100

        Row{
            anchors.centerIn: parent
            spacing: 2
            Text {
                color: "#FFFFFF"
                font.pixelSize: 20
                text: qsTr("设置")
                verticalAlignment: Text.AlignVCenter
                anchors.verticalCenter: parent.verticalCenter
            }
            Image {
                width: 32
                height: 32
                anchors.verticalCenter: parent.verticalCenter
                source: "qrc:/image/icon/down.png"
            }
        }
        MouseArea{
            anchors.fill: parent
            hoverEnabled: true
            propagateComposedEvents: true
            onClicked: {
                settingMenu.open()
            }
            onEntered: {
                parent.color = "#454B57"
            }

            onExited: {
                parent.color = Qt.rgba(0,0,0,0)
            }
        }

    }

    //关于菜单
    Menu {
        id:aboutMenu
        x:about.x
        y:about.y+about.height+1
        width: 120
        Action {
            text: qsTr("关于这个")
            onTriggered: console.log("关于这个")
        }
        Action {
            text: qsTr("关于那个")
            onTriggered: console.log("关于那个")
        }

        delegate: MenuBarItem {
            id: aboutMenuItem
            height: 40
            width: 120

            contentItem: Text {
                text: aboutMenuItem.text
                font.pixelSize: 16
                color:  "#ffffff"
                horizontalAlignment: Text.AlignHCenter
                verticalAlignment: Text.AlignVCenter
                elide: Text.ElideRight
            }

            background: Rectangle {
                implicitWidth: 120
                implicitHeight: 40
                color: Qt.rgba(0,0,0,0)
                MouseArea{
                    anchors.fill: parent
                    hoverEnabled: true
                    propagateComposedEvents: true
                    onClicked: {
                        aboutMenu.close()
                        aboutMenuItem.action.trigger()
                    }
                    onEntered: {
                        parent.color = "#454B57"
                    }

                    onExited: {
                        parent.color = Qt.rgba(0,0,0,0)
                    }
                }

            }

        }
        background: Rectangle {
            implicitWidth: 150
            implicitHeight: 40
            color: "#1F2025"
        }
    }
    //关于
    Rectangle{
        id:about
        anchors.top: parent.top
        anchors.bottom: parent.bottom
        anchors.left:setting.right
        color: Qt.rgba(0,0,0,0)
        width: 100

        Row{
            anchors.centerIn: parent
            spacing: 2
            Text {
                color: "#FFFFFF"
                font.pixelSize: 20
                text: qsTr("关于")
                verticalAlignment: Text.AlignVCenter
                anchors.verticalCenter: parent.verticalCenter
            }
            Image {
                width: 32
                height: 32
                anchors.verticalCenter: parent.verticalCenter
                source: "qrc:/image/icon/down.png"
            }
        }
        MouseArea{
            anchors.fill: parent
            hoverEnabled: true
            propagateComposedEvents: true
            onClicked: {
                aboutMenu.open()
            }
            onEntered: {
                parent.color = "#454B57"
            }

            onExited: {
                parent.color = Qt.rgba(0,0,0,0)
            }
        }

    }
}
```

在[Butterfly-安裝文檔-三-主題配置](http://butterfly.js.org/posts/4aa8abbe/#代碼高亮主題)中已經介紹瞭如何使用自定義代碼配色。

這篇文章是使用自定義代碼配色的實例教程。

## Highlight

以使用`Kimbie dark`主題配色為例子

### 配置`hljs`為`true`

修改 Hexo 根目錄下的`_config.yml`

```yaml
highlight:
  enable: true
  line_number: true
  auto_detect: false
  tab_replace:
  hljs: true
```

### 配置`highlight_theme`為`false`

修改`主題配置文件`

```yaml
highlight_theme: false
```

### 下載和修改 CSS 文件

在[Github](https://github.com/highlightjs/highlight.js/blob/master/src/styles/kimbie.dark.css)上找到這個 CSS

在 Hexo 的根目錄`source`文件夾下創立一個`self`文件夾，在`self`文件夾創建一個`Kimbiedark.css`文件

把 Github 上`Kimbie dark`的 CSS 代碼複製到`Kimbiedark.css`去。（你也可以直接從 Github 上下載這個 CSS）

**Kimbiedark.css**代碼如下

```css
/*
    Name:     Kimbie (dark)
    Author:   Jan T. Sott
    License:  Creative Commons Attribution-ShareAlike 4.0 Unported License
    URL:      https://github.com/idleberg/Kimbie-highlight.js
*/

/* Kimbie Comment */
.hljs-comment,
.hljs-quote {
  color: #d6baad;
}

/* Kimbie Red */
.hljs-variable,
.hljs-template-variable,
.hljs-tag,
.hljs-name,
.hljs-selector-id,
.hljs-selector-class,
.hljs-regexp,
.hljs-meta {
  color: #dc3958;
}

/* Kimbie Orange */
.hljs-number,
.hljs-built_in,
.hljs-builtin-name,
.hljs-literal,
.hljs-type,
.hljs-params,
.hljs-deletion,
.hljs-link {
  color: #f79a32;
}

/* Kimbie Yellow */
.hljs-title,
.hljs-section,
.hljs-attribute {
  color: #f06431;
}

/* Kimbie Green */
.hljs-string,
.hljs-symbol,
.hljs-bullet,
.hljs-addition {
  color: #889b4a;
}

/* Kimbie Purple */
.hljs-keyword,
.hljs-selector-tag,
.hljs-function {
  color: #98676a;
}

.hljs {
  display: block;
  overflow-x: auto;
  background: #221a0f;
  color: #d3af86;
  padding: 0.5em;
}

.hljs-emphasis {
  font-style: italic;
}

.hljs-strong {
  font-weight: bold;
}
```

修改這個 CSS 文件為

```css
/*
    Name:     Kimbie (dark)
    Author:   Jan T. Sott
    License:  Creative Commons Attribution-ShareAlike 4.0 Unported License
    URL:      https://github.com/idleberg/Kimbie-highlight.js
*/

/* 新添加的內容
  ------------------------------------- 
  --hl-color                  代碼框字體顔色 【必須】 (把下面.hljs的 color複製到這裏來)
  --hl-bg                     代碼框背景色 【必須】 (把下面.hljs的 background複製到這裏來)
  --hltools-bg: #321a0f       代碼框頂部工具欄背景色 【可選】(如果你關掉了 copy、lang 和 shrink,可不用配置這個）
  --hltools-color: #fff       代碼框頂部工具欄字體顔色 【可選】(如果你關掉了 copy、lang 和 shrink,可不用配置這個）
  --hlnumber-bg: #221a0f      代碼框行數背景色 【可選】(如果已經關掉 line_number,可以不用配置這個)
  --hlnumber-color: #fff      代碼框行數字體顔色 【可選】 (如果已經關掉 line_number,可以不用配置這個)
  --hlscrollbar-bg: #d3af86   代碼框滾動條顔色 【可選】（默認為主題主顔色）
  --hlexpand-bg: #d3af86      代碼框底部展開背景色 【可選】(如果已經關掉 highlight_height_limit,可以不用配置這個)
*/

:root {
  --hl-color: #d3af86;
  --hl-bg: #221a0f;
  --hltools-bg: #321a0f;
  --hltools-color: #fff;
  --hlnumber-bg: #221a0f;
  --hlnumber-color: #fff;
  --hlscrollbar-bg: #d3af86;
  --hlexpand-bg: #d3af86;
}

/* Kimbie Comment */
.hljs-comment,
.hljs-quote {
  color: #d6baad;
}

/* Kimbie Red */
.hljs-variable,
.hljs-template-variable,
.hljs-tag,
.hljs-name,
.hljs-selector-id,
.hljs-selector-class,
.hljs-regexp,
.hljs-meta {
  color: #dc3958;
}

/* Kimbie Orange */
.hljs-number,
.hljs-built_in,
.hljs-builtin-name,
.hljs-literal,
.hljs-type,
.hljs-params,
.hljs-deletion,
.hljs-link {
  color: #f79a32;
}

/* Kimbie Yellow */
.hljs-title,
.hljs-section,
.hljs-attribute {
  color: #f06431;
}

/* Kimbie Green */
.hljs-string,
.hljs-symbol,
.hljs-bullet,
.hljs-addition {
  color: #889b4a;
}

/* Kimbie Purple */
.hljs-keyword,
.hljs-selector-tag,
.hljs-function {
  color: #98676a;
}

/* 更改的內容 把.hljs改為 #article-container figure.highlight .hljs *、
/* ------------------------------------- */
#article-container figure.highlight .hljs {
  display: block;
  overflow-x: auto;
  background: #221a0f;
  color: #d3af86;
  padding: 0.5em;
}

.hljs-emphasis {
  font-style: italic;
}

.hljs-strong {
  font-weight: bold;
}
```

### 引入文件

修改`主題配置文件`

```yaml
inject:
  head:
    - <link rel="stylesheet" href="/self/Kimbiedark.css">
```

### 運行 Hexo

運行 Hexo 後，應該可以看到修改的效果

![hexo-theme-butterfly-doc-highlight-self-css.png](https://oss.012700.xyz/butterfly/2024/09/hexo-theme-butterfly-doc-highlight-self-css.png)

## Prismjs

以使用`prism-duotone-light`主題配色為例子

### 配置`highlight_theme`為`false`

修改`主題配置文件`

```yaml
highlight_theme: false
```

### 下載和修改 CSS 文件

在[Github](https://github.com/PrismJS/prism-themes/blob/master/themes/prism-duotone-light.css)上找到這個 CSS

在 Hexo 的根目錄`source`文件夾下創立一個`self`文件夾，在`self`文件夾創建一個`duotone.css`文件

把 Github 上`prism-duotone-light`的 CSS 代碼複製到``duotone.css`去。（你也可以直接從 Github 上下載這個 CSS）

**prism-duotone-light**代碼如下

```css
/*
Name:   Duotone Light
Author: Simurai, adapted from DuoTone themes for Atom (http://simurai.com/projects/2016/01/01/duotone-themes)

Conversion: Bram de Haan (http://atelierbram.github.io/Base2Tone-prism/output/prism/prism-base2tone-morning-light.css)
Generated with Base16 Builder (https://github.com/base16-builder/base16-builder)
*/

code[class*='language-'],
pre[class*='language-'] {
  font-family: Consolas, Menlo, Monaco, 'Andale Mono WT', 'Andale Mono', 'Lucida Console', 'Lucida Sans Typewriter',
    'DejaVu Sans Mono', 'Bitstream Vera Sans Mono', 'Liberation Mono', 'Nimbus Mono L', 'Courier New', Courier,
    monospace;
  font-size: 14px;
  line-height: 1.375;
  direction: ltr;
  text-align: left;
  white-space: pre;
  word-spacing: normal;
  word-break: normal;

  -moz-tab-size: 4;
  -o-tab-size: 4;
  tab-size: 4;

  -webkit-hyphens: none;
  -moz-hyphens: none;
  -ms-hyphens: none;
  hyphens: none;
  background: #faf8f5;
  color: #728fcb;
}

pre > code[class*='language-'] {
  font-size: 1em;
}

pre[class*='language-']::-moz-selection,
pre[class*='language-'] ::-moz-selection,
code[class*='language-']::-moz-selection,
code[class*='language-'] ::-moz-selection {
  text-shadow: none;
  background: #faf8f5;
}

pre[class*='language-']::selection,
pre[class*='language-'] ::selection,
code[class*='language-']::selection,
code[class*='language-'] ::selection {
  text-shadow: none;
  background: #faf8f5;
}

/* Code blocks */
pre[class*='language-'] {
  padding: 1em;
  margin: 0.5em 0;
  overflow: auto;
}

/* Inline code */
:not(pre) > code[class*='language-'] {
  padding: 0.1em;
  border-radius: 0.3em;
}

.token.comment,
.token.prolog,
.token.doctype,
.token.cdata {
  color: #b6ad9a;
}

.token.punctuation {
  color: #b6ad9a;
}

.token.namespace {
  opacity: 0.7;
}

.token.tag,
.token.operator,
.token.number {
  color: #063289;
}

.token.property,
.token.function {
  color: #b29762;
}

.token.tag-id,
.token.selector,
.token.atrule-id {
  color: #2d2006;
}

code.language-javascript,
.token.attr-name {
  color: #896724;
}

code.language-css,
code.language-scss,
.token.boolean,
.token.string,
.token.entity,
.token.url,
.language-css .token.string,
.language-scss .token.string,
.style .token.string,
.token.attr-value,
.token.keyword,
.token.control,
.token.directive,
.token.unit,
.token.statement,
.token.regex,
.token.atrule {
  color: #728fcb;
}

.token.placeholder,
.token.variable {
  color: #93abdc;
}

.token.deleted {
  text-decoration: line-through;
}

.token.inserted {
  border-bottom: 1px dotted #2d2006;
  text-decoration: none;
}

.token.italic {
  font-style: italic;
}

.token.important,
.token.bold {
  font-weight: bold;
}

.token.important {
  color: #896724;
}

.token.entity {
  cursor: help;
}

pre > code.highlight {
  outline: 0.4em solid #896724;
  outline-offset: 0.4em;
}

/* overrides color-values for the Line Numbers plugin
 * http://prismjs.com/plugins/line-numbers/
 */
.line-numbers .line-numbers-rows {
  border-right-color: #ece8de;
}

.line-numbers-rows > span:before {
  color: #cdc4b1;
}

/* overrides color-values for the Line Highlight plugin
 * http://prismjs.com/plugins/line-highlight/
 */
.line-highlight {
  background: rgba(45, 32, 6, 0.2);
  background: -webkit-linear-gradient(left, rgba(45, 32, 6, 0.2) 70%, rgba(45, 32, 6, 0));
  background: linear-gradient(to right, rgba(45, 32, 6, 0.2) 70%, rgba(45, 32, 6, 0));
}
```

修改這個 CSS 文件為

```css
/* 新添加的內容
  ------------------------------------- 
  --hl-color                  代碼框字體顔色 【必須】 (把下面 pre[class*="language-"]的 color 複製到這裏來)
  --hl-bg                     代碼框背景色 【必須】 (把下面 pre[class*="language-"]的 background複製到這裏來)
  --hltools-bg: #321a0f       代碼框頂部工具欄背景色 【可選】(如果你關掉了 copy、lang 和 shrink,可不用配置這個）
  --hltools-color: #fff       代碼框頂部工具欄字體顔色 【可選】(如果你關掉了 copy、lang 和 shrink,可不用配置這個）
  --hlnumber-bg: #221a0f      代碼框行數背景色 【可選】(如果已經關掉 line_number,可以不用配置這個)
  --hlnumber-color: #fff      代碼框行數字體顔色 【可選】 (如果已經關掉 line_number,可以不用配置這個)
  --hlscrollbar-bg: #d3af86   代碼框滾動條顔色 【可選】（默認為主題主顔色）
  --hlexpand-bg: #d3af86      代碼框底部展開背景色 【可選】(如果已經關掉 highlight_height_limit,可以不用配置這個)
*/

:root {
  --hl-color: #728fcb;
  --hl-bg: #faf8f5;
  --hltools-bg: xxxxxxx;
  --hltools-color: xxxxxxx;
  --hlnumber-bg: xxxxxxx;
  --hlnumber-color: xxxxxxxx;
  --hlscrollbar-bg: xxxxx;
  --hlexpand-bg: xxxxxxx;
}

/* ------------------------------------- */

/* 這些可以刪除（可留着，如果有衝突，請刪除） */
/* ------------------------------------- */
code[class*='language-'],
pre[class*='language-'] {
  font-family: Consolas, Menlo, Monaco, 'Andale Mono WT', 'Andale Mono', 'Lucida Console', 'Lucida Sans Typewriter',
    'DejaVu Sans Mono', 'Bitstream Vera Sans Mono', 'Liberation Mono', 'Nimbus Mono L', 'Courier New', Courier,
    monospace;
  font-size: 14px;
  line-height: 1.375;
  direction: ltr;
  text-align: left;
  white-space: pre;
  word-spacing: normal;
  word-break: normal;

  -moz-tab-size: 4;
  -o-tab-size: 4;
  tab-size: 4;

  -webkit-hyphens: none;
  -moz-hyphens: none;
  -ms-hyphens: none;
  hyphens: none;
  background: #faf8f5;
  color: #728fcb;
}

pre > code[class*='language-'] {
  font-size: 1em;
}

pre[class*='language-']::-moz-selection,
pre[class*='language-'] ::-moz-selection,
code[class*='language-']::-moz-selection,
code[class*='language-'] ::-moz-selection {
  text-shadow: none;
  background: #faf8f5;
}

pre[class*='language-']::selection,
pre[class*='language-'] ::selection,
code[class*='language-']::selection,
code[class*='language-'] ::selection {
  text-shadow: none;
  background: #faf8f5;
}

/* Code blocks */
pre[class*='language-'] {
  padding: 1em;
  margin: 0.5em 0;
  overflow: auto;
}

/* Inline code */
:not(pre) > code[class*='language-'] {
  padding: 0.1em;
  border-radius: 0.3em;
}
/* ------------------------------------- */
/* 到這裏為止，可以刪除 */

.token.comment,
.token.prolog,
.token.doctype,
.token.cdata {
  color: #b6ad9a;
}

.token.punctuation {
  color: #b6ad9a;
}

.token.namespace {
  opacity: 0.7;
}

.token.tag,
.token.operator,
.token.number {
  color: #063289;
}

.token.property,
.token.function {
  color: #b29762;
}

.token.tag-id,
.token.selector,
.token.atrule-id {
  color: #2d2006;
}

code.language-javascript,
.token.attr-name {
  color: #896724;
}

code.language-css,
code.language-scss,
.token.boolean,
.token.string,
.token.entity,
.token.url,
.language-css .token.string,
.language-scss .token.string,
.style .token.string,
.token.attr-value,
.token.keyword,
.token.control,
.token.directive,
.token.unit,
.token.statement,
.token.regex,
.token.atrule {
  color: #728fcb;
}

.token.placeholder,
.token.variable {
  color: #93abdc;
}

.token.deleted {
  text-decoration: line-through;
}

.token.inserted {
  border-bottom: 1px dotted #2d2006;
  text-decoration: none;
}

.token.italic {
  font-style: italic;
}

.token.important,
.token.bold {
  font-weight: bold;
}

.token.important {
  color: #896724;
}

.token.entity {
  cursor: help;
}

